import React from 'react';
import './App.css';

class App extends React.Component {
  state = {
    userToFetch: "pjose92"
  }

  componentDidMount() {
    fetch(`https://api.github.com/users/${this.state.userToFetch}`)
    .then(res => res.json())
    .then(user => this.setState({...this.state, user: user}))
    .catch(err => console.log("Error in the componentDidMount: ", err));
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.userSoFetch !== this.state.userToFetch) {
      fetch(`https://api.github.com/users/${this.state.userToFetch}`)
      .then(res => res.json())
      .then(user => this.setState({...this.state, user: user }))
      .then(() => this.fetchFollowers(this.state.userToFetch))
      .catch(err => console.log("Error in componentDidUpdate: ", err));
    }
  }


}



export default App;
